 void kitap_turleri_getir()
    {
        kitap_turleri.removeAllItems();
          try{
           // while(personelSonucKumesi.next()){personelCombo.addItem(personelSonucKumesi.getString(“Adi”));
         
           String sql ="select * from kitap_turleri";
           pst=conn.prepareStatement(sql);
           rs=pst.executeQuery();
           int say=0;
           
           while(rs.next())
           {
           kitap_turleri.addItem(rs.getString("kitap_tur"));
           say++;
           }
        
           if(say==0)
           {
               kitap_turleri.addItem("KİTAP TÜRÜ EKLEYİNİZ !");
               kaydet.setEnabled(false);
               temizle.setEnabled(false);
           }
           else
           {
               kaydet.setEnabled(true);
               temizle.setEnabled(true);
           }
           
        }catch(SQLException e)
        {
        JOptionPane.showMessageDialog(null,"VERİ TABANI BAĞLANTISI BAŞARISIZ !: " + e.getSQLState());
        }
    }
 
