    private void kaydetActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_kaydetActionPerformed
       
    try{
            String baslik = kitap_baslik.getText().trim();
            String yazar = kitap_yazar.getText().trim();
            String tur = (String) kitap_turleri.getSelectedItem();
            int tur_id=-1;
            
           String sql0 ="select * from kitap_turleri where kitap_tur='"+tur+"'";
           pst=conn.prepareStatement(sql0);
           rs=pst.executeQuery();
           
           while(rs.next())
           {
           tur_id=rs.getInt("kitap_turleri_id");
           }
           pst.close();
           rs.close();
           
           
            String isbn = kitap_isbn.getText().trim();
            String baski_sayi =  (String) kitap_sayi.getSelectedItem();
            String baski_yil = kitap_yili.getText().trim();
            String yayinevi = kitap_yayinevi.getText().trim();
            String sayfa_sayi = kitap_sayfa_sayisi.getText().trim();
            String fiyat = kitap_fiyat.getText().trim();
            
            boolean baski_yil_kontrol = genelfonksiyonlar.int_sayimi(baski_yil);
            boolean sayfa_sayi_kontrol = genelfonksiyonlar.int_sayimi(sayfa_sayi);
            boolean fiyat_kontrol = genelfonksiyonlar.double_sayimi(fiyat);
            
  String sql ="insert into kitaplar (kitap_baslik,kitap_yazar,kitap_turleri_id,kitap_isbn,kitap_baski_sayi,kitap_baski_yil,kitap_yayinevi,kitap_sayfa_sayi,kitap_fiyat,kitap_mevcutmu) values (?,?,?,?,?,?,?,?,?,1)";
  String sql1 = "select * from kitaplar where kitap_isbn='"+isbn+"'";
  
  if( !(baslik.isBlank()|| yazar.isBlank() || isbn.isBlank() ||  baski_yil_kontrol==false || yayinevi.isBlank() || sayfa_sayi_kontrol==false || fiyat_kontrol==false  ) )
          {
   pst=conn.prepareStatement(sql1);
   rs=pst.executeQuery();
   
   if(rs.next())
   {
       JOptionPane.showMessageDialog(null, "Kitap eklenemedi ! -> Kitap Mevcut !");
   }
   else
   {
   pst = conn.prepareStatement(sql);
   pst.setString(1,baslik);
   pst.setString(2,yazar);
   pst.setInt(3,tur_id);
   pst.setString(4,isbn);
   pst.setInt(5,Integer.parseInt(baski_sayi));
   pst.setInt(6,Integer.parseInt(baski_yil));
   pst.setString(7,yayinevi);
   pst.setInt(8,Integer.parseInt(sayfa_sayi));
   pst.setDouble(9,Double.parseDouble(fiyat));
   pst.execute();
   JOptionPane.showMessageDialog(null, "Kitap ekleme işlemi başarılı !");
   pst.close();
        }
   rs.close();     
          }
  else
  {
   JOptionPane.showMessageDialog(null, "Kitap eklenemedi ! -> Geçersiz bir değer girişi veya Boş Alan");
  }
   }
        catch(SQLException e)
        {
            JOptionPane.showMessageDialog(null,"VERİ TABANI BAĞLANTISI BAŞARISIZ !: " + e.getSQLState());
        }
        
    }//GEN-LAST:event_kaydetActionPerformed

